```{.r .cell-code}
coups |>
  # add one row which adds up the total
  arrange(desc(`Coup attempted`)) |>
  head(11) |> 
  add_row(
    country = "Total",
    `Coup attempted` = sum(coups[, 2]),
    `Coup succeeded` = sum(coups[, 3])
  ) |>
  mutate(Success_rate = percent(`Coup succeeded` / `Coup attempted`, 
                                accuracy = 0.1
                                )) |>   
  clean_names(case = "title") |>
